mapper.select:
select (@i:=@i+1) rownum,n.* from tb_caas_template n , (select @i:=#{start} )it where 1=1
and (n.userId like concat('%',#{condition},'%') n.templateName like concat('%',#{condition},'%') or n.id = #{condition})
and n.status=#{status}
${limit}

select count(0) as totalCount from tb_caas_template n where 1=1
and (n.userId like concat('%',#{condition},'%') n.templateName like concat('%',#{condition},'%') or n.id = #{condition})
and n.status=#{status}
